import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { url } from '../../src/config/constants';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  constructor(private http: HttpClient) {}
  isAuthenticated: string = localStorage.getItem('isAuthenticated');

  // Create a new user
  saveUser(userData: any): Observable<any> {
    console.log(`userData: ${JSON.stringify(userData)}`);
    return this.http.post(url.domainurl + `users`, userData);
  }

  //Login
  login(loginData: any): Observable<any> {
    console.log(`loginData: ${JSON.stringify(loginData)}`);
    return this.http.post(url.domainurl + `auth/login`, loginData).pipe(
      map((data: any) => {
        alert(data['message']);
        if (data['message'] == 'Login successful') {
          localStorage.setItem('isAuthenticated', 'true');
        }else{
          localStorage.setItem('isAuthenticated', 'false');
        }
        return data;
      })
    );
  }
}
