import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { url } from '../../src/config/constants';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  constructor(private http: HttpClient) {}
  isAuthenticated: boolean = false;
  // Create a new user
  saveUser(userData: any): Observable<any> {
    console.log(`userData: ${JSON.stringify(userData)}`);
    return this.http.post(url.domainurl + `users`, userData);
  }

  //Login
  login(loginData: any): Observable<any> {
    console.log(`loginData: ${JSON.stringify(loginData)}`);
    var response: any = this.http.post(url.domainurl + `auth/login`, loginData);
    if (response['message'] == 'Login successful') this.isAuthenticated = true;
    alert(this.isAuthenticated);
    return response;
  }
}
